    }

    render() {
        console.log(this.state.id)
    return(
        <BrowserRouter>
            <div className='App'>
                <div className='left'>
                    <Route path={"/"} render={(props) => {
                        return <PlayerSection {...props}/>
                    }}/>
                </div>
                <div className='right'>
                    <Route path={"/"} render={(props) => {
                        return <PlaylistSection parentState={this.changeId} {...props}/>
                    }}/>
                </div>
            </div>
        </BrowserRouter>
    )}
}

export default App;
