import React from 'react';
import { Link } from 'react-router-dom'

import classes from './VideoCard.module.css';

class VideoCard extends React.Component {

    handleClick = () => {
        this.state = {
            ElementStyle : {
                color : 'yellow'
            }
        }
    }

    render() {
        return(
            <Link to={`/videoId=${this.props.vidId}`} className={classes.link}>
                <div onClick={this.handleClick} className={classes.CardContainer}>
                    <img className={classes.Thumbnail} src={this.props.thumbnail} alt="Video Thumbnail" />
                    <h3 className={classes.Title}>{this.props.title}</h3>
                </div>
            </Link>
        );
    }
}

export default VideoCard;
